mapper.select:
select * from (
	  select rownum as rnum, a.* from (
	    select a.approval_seq, a.member_email, a.approval_title, a.approval_content, a.approval_at, a.approval_status, m.name as member_name, m.dept_code, d.dept_name, m.level_code, l.level_name
	    from approval a
	    join member m on a.member_email = m.email
	    join department d on m.dept_code = d.dept_code
	    join job_level l on m.level_code = l.level_code and m.company_code = l.company_code
	    where m.company_code = #{company_code}
	    and a.approval_status = #{status}
	    and m.dept_code = #{deptCode}
	    order by a.approval_seq desc
	  ) a where rownum <= #{end}
	) where rnum >= #{start}

    select count(*)
	from approval a
	join member m on a.member_email = m.email
	where m.company_code = #{company_code}
	and a.approval_status = #{status}
	and m.dept_code = #{deptCode}

mapper.update:
UPDATE approval
SET approval_status = #{newStatus}
WHERE approval_seq = #{targetseq}
