mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT a.approval_seq, a.member_email, a.approval_title, a.approval_content, a.approval_at, a.approval_status, m.name AS member_name, m.dept_code, m.level_code FROM approval a JOIN member m ON a.member_email = m.email
AND a.approval_status = #{status}
AND m.dept_code = #{deptCode}
ORDER BY a.approval_seq DESC ) A WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

SELECT COUNT(*)
FROM approval a
JOIN member m ON a.member_email = m.email


    AND a.approval_status = #{status}


    AND m.dept_code = #{deptCode}

mapper.update:
UPDATE approval
SET approval_status = #{newStatus}
WHERE approval_seq = #{targetseq}
